const now = new Date();
const startOfWeek = new Date(now);
startOfWeek.setDate(now.getDate() - now.getDay()); // Đặt về Chủ nhật
const monday = new Date(startOfWeek);
monday.setDate(startOfWeek.getDate() + 1); // Thứ Hai
const saturday = new Date(startOfWeek);
saturday.setDate(startOfWeek.getDate() + 6); // Thứ Bảy

return [{
  json: {
    monday: monday.toLocaleDateString('vi-VN', { day: '2-digit', month: '2-digit', year: 'numeric' }),
    saturday: saturday.toLocaleDateString('vi-VN', { day: '2-digit', month: '2-digit', year: 'numeric' })
  }
}];
